package com.syntax.class03;

public class Recap {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		/*
		 * String concatention helps us to combine a Strings with any primative data type together
		 * 
		 * 
		 */

		String firstName="Mina";
		String lastName="Henen";
		String fullName=firstName+" "+lastName;
		System.out.println(fullName);
		
		int houseNo=10;
		int apartmentNo=5;
		String streetName="That street";
		String city="New York";
		String country="USA";
		String fullAddress=houseNo+" "+apartmentNo+" "+streetName+" "+city+" "+country;
		System.out.println(fullAddress);
		
	
		
				
		
	}

}
