package com.syntax.class03;

public class RecapOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		/*
		 * Addition => +
		 * Subtraction => -
		 * Multiplication=> *
		 * Division=> /
		 * Modulus/Remainder=> %
		 */
		
		int number1=8;
		int number2=2;
		System.out.println("Answer = "+number1/number2); //dividing number 1 by number2
		System.out.println("Remainder = "+number1%number2);
		
		double number3=9;
		double number4=2;
		System.out.println(number3/number4); //dividing number 3 by number 4
		
		
		float number5=1.0f;
		float number6=3.0f;
		System.out.println(number5/number6); //dividing number 5 by number 6
		
		double number7=1;
		double number8=3;
		System.out.println(number7/number8); //dividing number 5 by number 6
		
		
	}

}
